const del = require('del');
const validateOptions = require('schema-utils');
const path = require('path');
const schema = {
  type: 'object',
  properties: {
    exclude: {
      type: 'string'
    }
  }
};

module.exports = class EmptyPlugin {
  constructor(options = {exclude: ''}) {
    this.options = options;
    validateOptions(schema, options, 'Empty Plugin');
  }

  apply(compiler) {
    compiler.hooks.emit.tapAsync('EmptyPlugin', (complation, callback) => {
      let delFiles = [`${compiler.options.output.path}/**`, `!${compiler.options.output.path}/${this.options.exclude}`];
      del(delFiles)
        .then(() => {
          callback()
        });
    });
  }
}